INSERT INTO `auto_code`.`template_config` (`name`, `save_path`, `suffix`, `file_name`, `content`, `back_user`) VALUES ('api_add_model', 'api', NULL, 'Add${context.javaBeanName}.java', 'package ${context.packageName};\n\nimport java.util.HashMap;\nimport java.util.Map;\n\nimport com.sanqing.page.CheckText;\nimport com.sanqing.page.PageConfig;\nimport com.sanqing.sca.service.BaseAgent;\nimport com.sanqing.sca.service.Protocol;\nimport com.sanqing.sca.service.ReturnCode;\n/**\n * 添加${table.comment}\n * @author ${username}\n * @time ${context.createTime}\n * @version 1.0\n */\npublic class Add${context.javaBeanName} extends BaseAgent{\n	@Override\n	public Protocol execute(Protocol protocol) throws Exception{\n    	Map<String, String> requestMap = protocol.getData(PageConfig.PAGE_REQUEST);\n#foreach($column in $columns)\n#if(!${column.isPk})\n		String ${column.columnName} = requestMap.get(\"${column.columnName}\");\n		CheckText.checkFormat(${column.columnName},\"${column.checkType}\",#if(${column.checkType}==\'N\' && ${column.precision}!=\'0\')${column.precision}#else${column.length}#end,${column.scale},${column.isNull},\"${column.comment}\");\n#end\n#end\n		//添加${table.comment}到数据库\n		Map<String, Object> map = new HashMap<String, Object>();\n#foreach($column in $columns)\n#if(${column.isPk})\n		long ${column.columnName} = dao.getNextval(\"SEQ_GG_PUBLIC\");\n#end\n		map.put(\"${column.columnName}\", ${column.columnName});\n#end\n		dao.add(\"${table.tableName}\", map);\n		\n		protocol.setRecode(ReturnCode.SUCCESS_CODE);\n		protocol.setRemsg(ReturnCode.SUCCESS_REMSG);\n		return protocol;\n	}\n}\n', 'admin');
INSERT INTO `auto_code`.`template_config` (`name`, `save_path`, `suffix`, `file_name`, `content`, `back_user`) VALUES ('api_modify_model', 'api', NULL, 'Modify${context.javaBeanName}.java', 'package ${context.packageName};\n\nimport java.util.HashMap;\nimport java.util.Map;\n\nimport com.sanqing.page.CheckText;\nimport com.sanqing.page.PageConfig;\nimport com.sanqing.sca.service.BaseAgent;\nimport com.sanqing.sca.service.Protocol;\nimport com.sanqing.sca.service.ReturnCode;\n\n/**\n * 修改${table.comment}\n * \n * @author ${username}\n * @time ${context.createTime}\n * @version 1.0\n */\npublic class Modify${context.javaBeanName} extends BaseAgent {\n\n	@Override\n	public Protocol execute(Protocol protocol) throws Exception {\n\n		Map<String, String> requestMap = protocol\n				.getData(PageConfig.PAGE_REQUEST);\n#foreach($column in $columns)\n		String ${column.columnName} = requestMap.get(\"${column.columnName}\");\n		CheckText.checkFormat(${column.columnName},\"${column.checkType}\",#if(${column.checkType}==\'N\' && ${column.precision}!=\'0\')${column.precision}#else${column.length}#end,${column.scale},${column.isNull},\"${column.comment}\");\n#end\n		// 根据主键修改对象\n		Map<String, Object> map = new HashMap<String, Object>();\n		Map<String, Object> conditionMap = new HashMap<String, Object>();\n		// 构建更新对象map\n		// 构建更新表条件conditionMap\n#foreach($column in $columns)\n#if(${column.isPk})\n		conditionMap.put(\"${column.columnName}\", ${column.columnName});\n#else\n		map.put(\"${column.columnName}\", ${column.columnName});\n#end\n#end\n		// 更新表\n		dao.update(\"${table.tableName}\", map, conditionMap);\n		protocol.setRecode(ReturnCode.SUCCESS_CODE);\n		protocol.setRemsg(ReturnCode.SUCCESS_REMSG);\n\n		return protocol;\n	}\n}', 'admin');
INSERT INTO `auto_code`.`template_config` (`name`, `save_path`, `suffix`, `file_name`, `content`, `back_user`) VALUES ('api_query_model', 'api', NULL, 'Query${context.javaBeanName}.java', 'package ${context.packageName};\n\nimport java.util.ArrayList;\nimport java.util.List;\nimport java.util.Map;\n\nimport com.sanqing.page.CheckText;\nimport com.sanqing.page.PageConfig;\nimport com.sanqing.page.SplitPage;\nimport com.sanqing.sca.service.BaseAgent;\nimport com.sanqing.sca.service.Protocol;\nimport com.sanqing.sca.service.ReturnCode;\n/**\n * 查询${table.comment}\n * @author ${username}\n * @time ${context.createTime}\n * @version 1.0\n */\npublic class Query${context.javaBeanName} extends BaseAgent{\n	@Override\n	public Protocol execute(Protocol protocol) throws Exception{\n				\n		Map<String,String> requestMap = protocol.getData(PageConfig.PAGE_REQUEST);\n		//数据合法性校验\n#foreach($column in $columns)\n		String q${column.columnName} = requestMap.get(\"q${column.columnName}\");\n		CheckText.checkFormat(q${column.columnName},\"${column.checkType}\",#if(${column.checkType}==\'N\' && ${column.precision}!=\'0\')${column.precision}#else${column.length}#end,${column.scale},${column.isNull},\"${column.comment}\");\n#end\n		String whereStr = \"\";\n		String orderbyStr = \"  order by t.${pkColumn.columnName} desc\";\n		List<Object> searchParameterList = new ArrayList<Object>();\n#foreach($column in $columns)\n		if(q${column.columnName} != null && !\"\".equals(q${column.columnName})){\n			whereStr = whereStr + \" and t.${column.columnName} = ? \";\n			searchParameterList.add(q${column.columnName});\n		}\n#end\n		String sql = \"select * from ${table.tableName} t where 1=1 \"+ whereStr + orderbyStr;\n		SplitPage sp = dao.splitPage(SplitPage.getInstance(protocol.getData(PageConfig.PAGE_SPLIT)), sql, searchParameterList);\n		sp.buildProtocol(protocol);\n		protocol.setRecode(ReturnCode.SUCCESS_CODE);\n		protocol.setRemsg(ReturnCode.SUCCESS_REMSG);\n		return protocol;\n	}\n}', 'admin');
INSERT INTO `auto_code`.`template_config` (`name`, `save_path`, `suffix`, `file_name`, `content`, `back_user`) VALUES ('api_view_model', 'api', NULL, 'View${context.javaBeanName}.java', 'package ${context.packageName};\n\nimport java.util.ArrayList;\nimport java.util.List;\nimport java.util.Map;\nimport com.sanqing.exception.MyException;\nimport com.sanqing.page.CheckText;\nimport com.sanqing.page.PageConfig;\nimport com.sanqing.sca.service.BaseAgent;\nimport com.sanqing.sca.service.Protocol;\nimport com.sanqing.sca.service.ReturnCode;\n/**\n * 查看${table.comment}\n * @author ${username}\n * @time ${context.createTime}\n * @version 1.0\n */\npublic class View${context.javaBeanName} extends BaseAgent {\n	@Override\n	public Protocol execute(Protocol protocol) throws Exception {\n\n		Map<String, String> requestMap = protocol.getData(PageConfig.PAGE_REQUEST);\n		// 数据合法性校验\n		String ${pkColumn.columnName} = requestMap.get(\"${pkColumn.columnName}\");\n		CheckText.checkFormat(${pkColumn.columnName},\"${pkColumn.checkType}\",#if(${pkColumn.checkType}==\'N\' && ${pkColumn.precision}!=\'0\')${pkColumn.precision}#else${pkColumn.length}#end,${pkColumn.scale},${pkColumn.isNull},\"${pkColumn.comment}\");\n\n		// 查询指定对象返回\n		String sql = \"select * from ${table.tableName} t where t.${pkColumn.columnName} = ?\";\n		Map<String, Object> map = dao.queryForMap(sql, ${pkColumn.columnName});\n\n		if (map == null || map.isEmpty()) {\n			throw new MyException(ReturnCode.TASK_EXCEPTION_CODE, \"没有找到相应的数据！\");\n		}\n\n		List<Map<String, Object>> reList = new ArrayList<Map<String, Object>>();\n		reList.add(map);\n		protocol.putTable(\"getObj\", reList);\n\n		protocol.setRecode(ReturnCode.SUCCESS_CODE);\n		protocol.setRemsg(ReturnCode.SUCCESS_REMSG);\n\n		return protocol;\n	}\n}', 'admin');
