INSERT INTO `auto_code`.`template_config` (`name`, `save_path`, `suffix`, `file_name`, `content`, `back_user`) VALUES ('jsp_modify_model', 'jsp', NULL, 'modify${context.javaBeanName}.jsp', '#set($myif=\"c:if\")\n#set($myfor=\"c:forEach\")\n#set($jq=\"$\")\n<%@page import=\"com.sanqing.util.PageSendRedirect\"%>\n<%@page import=\"com.sanqing.sca.service.Protocol\"%>\n<%@page import=\"com.sanqing.util.RequestUtil\"%>\n<%@page import=\"com.sanqing.upload.UploadConfig\"%>\n<%@ page language=\"java\" import=\"java.util.*\" pageEncoding=\"UTF-8\"%>\n<%@ taglib prefix=\"c\" uri=\"http://java.sun.com/jsp/jstl/core\"%>\n<%@ taglib prefix=\"sq\" uri=\"/WEB-INF/tlds/sanqing.tld\"%>\n<%@ taglib prefix=\"fmt\" uri=\"http://java.sun.com/jsp/jstl/fmt\"%>\n<%@ taglib prefix=\"selectZidian\" uri=\"/WEB-INF/tlds/selectZidian.tld\"%>\n<%\n	RequestUtil requestUtil = new RequestUtil();	\n	Protocol protocol = requestUtil.pageRequest(\"${context.service}\",\"View${context.javaBeanName}\",false,request);\n	\n	if(protocol == null){\n		PageSendRedirect.PageError(request,response,\"-1\",\"服务调用失败\",\"/${context.javaBeanNameLC}/query${context.javaBeanName}.do\");\n		return;\n	}\n	if(!\"0\".equals(protocol.getRecode())){\n		PageSendRedirect.PageError(request,response,protocol.getRecode(),protocol.getRemsg(),\"/${context.javaBeanNameLC}/query${context.javaBeanName}.do\");\n		return;\n	}\n	List<Map<String, Object>> list = protocol.getTable().get(\"getObj\");\n	if(list.size() <= 0){\n		PageSendRedirect.PageError(request,response,\"9999\",\"没有找到对应记录\",\"/${context.javaBeanNameLC}/query${context.javaBeanName}.do\");\n		return;\n	}\n	Map<String, Object> map = new HashMap<String, Object>();\n	map = list.get(0);\n	request.setAttribute(\"modify\",map);\n%>\n<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\n<head>\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\" />\n<title>修改${table.comment}</title>\n\n<link rel=\"stylesheet\" type=\"text/css\" href=\"${base}/css/right.css\" />\n<link rel=\"stylesheet\" type=\"text/css\" href=\"${base}/third/upload/css/default.css\" />\n<link href=\"${base}/third/upload/css/fileinput.css\" media=\"all\" rel=\"stylesheet\" type=\"text/css\" />\n<!--[if IE]>\n		<script src=\"${base}/third/bootstrap-3.3.5/js/html5shiv.js\"></script>\n		<script src=\"${base}/third/bootstrap-3.3.5/js/respond.min.js\"></script>\n<![endif]-->\n<link rel=\"stylesheet\" href=\"${base}/third/kindeditor-4.0.1/themes/default/default.css\" />\n<link rel=\"stylesheet\" href=\"${base}/third/kindeditor-4.0.1/plugins/code/prettify.css\" />\n<link rel=\"stylesheet\" href=\"${base}/third/bootstrap-3.3.5/css/bootstrap.min.css\" />\n\n<script type=\'text/javascript\' src=\'${base}/js/CheckText.js\'></script>\n<script type=\'text/javascript\' src=\'${base}/js/checkbox.js\'></script>\n<script type=\'text/javascript\' src=\'${base}/js/verify.js\'></script>\n<script type=\'text/javascript\' src=\'${base}/third/bootstrap-3.3.5/js/jquery-1.10.2.min.js\'></script>\n<script type=\'text/javascript\' src=\'${base}/js/common.js\'></script>\n<script type=\'text/javascript\' src=\'${base}/js/calendar.js\'></script>\n<script src=\"${base}/third/upload/js/fileinput.js\" type=\"text/javascript\"></script>\n<script src=\"${base}/third/upload/js/fileinput_locale_zh.js\" type=\"text/javascript\"></script>\n<script charset=\"utf-8\" src=\"${base}/third/kindeditor-4.0.1/kindeditor.js\"></script>\n<script charset=\"utf-8\" src=\"${base}/third/kindeditor-4.0.1/lang/zh_CN.js\"></script>\n<script charset=\"utf-8\" src=\"${base}/third/kindeditor-4.0.1/plugins/code/prettify.js\"></script>\n<script charset=\"utf-8\" src=\"${base}/third/bootstrap-3.3.5/js/bootstrap.min.js\"></script>\n<script type=\"text/javascript\">\n	//验证添加信息并保存数据\n	function edit(e) {\n		//使用富文本打开注释\n        //editor1.sync();\n#foreach($column in $columns)\n		if (!checkFormItem(getById(\"${column.columnName}\"),\"${column.checkType}\",#if(${column.checkType}==\'N\' && ${column.precision}!=\'0\')${column.precision}#else${column.length}#end,${column.scale},${column.isNull},\"${column.comment}\"))return false;\n#end\n		disBut(e);\n        //不带上传\n        postData(\"${context.service}\",\"Modify${context.javaBeanName}\",\"DFORM\",\"query${context.javaBeanName}.do\");\n        //带上传控件\n		/*getById(\"DFORM\").action=\"add${context.javaBeanName}Result.do\";\n		getById(\"DFORM\").submit();*/\n	}\n\n	//返回\n	function back() {\n		window.location.href = \"query${context.javaBeanName}.do\";\n	}\n	var editor1;\n	$(function(){\n		//富文本控件修改相应id 打开注释\n		/*\n        editor1 = KindEditor.create(\'textarea[id=\"xxms\"]\', {\n			allowFileManager: true, \n		    filterMode: false,\n		    urlType:\'domain\',\n			cssPath : \'${base}/third/kindeditor-4.0.1/plugins/code/prettify.css\',\n			uploadJson : \'${base}/third/kindeditor-4.0.1/jsp/upload_json.jsp\',\n			fileManagerJson : \'${base}/third/kindeditor-4.0.1/jsp/file_manager_json.jsp\',\n			items : [\'undo\',\'redo\',\'|\',\'cut\',\'copy\',\'paste\',\'plainpaste\',\'wordpaste\',\'selectall\',\'|\',\n			         \'justifyleft\',\'justifycenter\',\'justifyright\',\'justifyfull\',\'|\',\'insertorderedlist\',\n			         \'insertunorderedlist\',\'indent\',\'outdent\',\'|\',\'subscript\',\'superscript\',\'formatblock\',\n			         \'fontname\',\'fontsize\',\'|\',\'forecolor\',\'hilitecolor\',\'bold\',\'italic\',\'underline\',\n			         \'strikethrough\',\'removeformat\',\'|\',\'fullscreen\',\'|\',\'image\',\'multiimage\',\'flash\',\'media\',\'insertfile\',\'|\',\'table\',\n			         \'hr\',\'pagebreak\',\'baidumap\',\'|\',\'link\',\'unlink\',\'anchor\',\'|\',\n			         \'emoticons\',\'|\',\'quickformat\',\'clearhtml\',\'|\',\'print\',\'|\',\'preview\',\'|\']\n		});\n		prettyPrint();\n        */\n		//上传控件修改为上传图片的ID,type=\"file\",打开注释\n		/*\n        var url=\"${jq}{modify[\'ZSTPDZ\']}\";\n		if(url==\'\'){\n			url=\'${base}/images/nophoto.jpg\';\n		}else{\n			url=\'<%=UploadConfig.UPLOAD_URL%>\'+url;\n		}\n		$(\"#zstpdz\").fileinput({\n			language: \'zh\',\n			initialPreview: [ \"<img src=\'\"+url+\"\' class=\'file-preview-image\' alt=\'专利图片展示\' title=\'专利图片展示\'>\",],\n			allowedPreviewTypes : [ \'image\' ],\n			allowedFileExtensions : [\'jpg\', \'png\',\'gif\',\'jpeg\'],\n			showUpload: false,\n			showCaption: false,\n			browseClass: \"btn btn-primary\"\n		});\n        */\n	});\n</script>\n</head>\n<body>\n	<div class=\"right_top\">\n		<div class=\"right_top_title\">当前位置：${table.comment}管理 &gt;${table.comment}管理 &gt; 修改</div>\n	</div>\n	<div class=\"right_mid\">\n		<div class=\"right_mid1\">\n			<div class=\"right_mid1_top\">\n				<div class=\"right_mid_title2\">${table.comment}详细信息</div>\n			</div>\n			<div class=\"right_mid1_mid\">\n				<form name=\"DFORM\" id=\"DFORM\" method=\"post\" enctype=\"multipart/form-data\">\n					<table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\n#set($i=0)\n#foreach($column in $columns)\n#if(!${column.isPk})\n#set($i=$i+1)\n#if($i==1 || $i%2==1)\n						<tr>\n#end\n                            <td width=\"15%\" align=\"right\">${column.comment}：</td>\n                            <td width=\"35%\">\n#if(${column.checkType}==\'D\')\n    							<input type=\"text\" id=\"${column.columnName}\" class=\"text\" name=\"${column.columnName}\" onfocus=\"calendar(event);\" value=\"${jq}{modify[\'${column.columnName}\']}\" style=\"width:200px;\"/>\n#else\n#if(${column.type}==\'CLOB\')\n								<textarea style=\"width:75%;height:300px;\" name=\"${column.columnName}\" id=\"${column.columnName}\">${jq}{modify[\'${column.columnName}\']}</textarea>\n#else\n                				<input type=\"text\" id=\"${column.columnName}\" name=\"${column.columnName}\" class=\"text\"  value=\"${jq}{modify[\'${column.columnName}\']}\"/>\n#end\n#if(${column.isNull}==\'1\')\n								&nbsp;<font color=\"red\">*</font>\n#end\n#end	\n							</td>\n#if($i==$columns.size() || $i%2==0)\n						</tr>\n#end\n#else\n						<input type=\"hidden\" id=\"${column.columnName}\" name=\"${column.columnName}\" value=\"${jq}{modify[\'${column.columnName}\']}\"/>\n#end\n#end\n						<tr>\n							<td colspan=\"4\" align=\"center\"><input type=\"button\"\n								class=\"but\" size=\"32\" value=\"确定\"\n								onclick=\"edit(this);return false;\" /> <input type=\"reset\"\n								class=\"but\" size=\"32\" value=\"重置\" /> <input type=\"button\"\n								class=\"but\" size=\"32\" value=\"返回\" onclick=\"back()\" /></td>\n						</tr>\n					</table>\n				</form>\n			</div>\n		</div>\n	</div>\n</body>\n</html>', 'admin');
INSERT INTO `auto_code`.`template_config` (`name`, `save_path`, `suffix`, `file_name`, `content`, `back_user`) VALUES ('jsp_query_model', 'jsp', NULL, 'query${context.javaBeanName}.jsp', '#set($myif=\"c:if\")\n#set($myfor=\"c:forEach\")\n#set($jq=\"$\")\n<%@page import=\"com.sanqing.mvc.config.AttrConfig\"%>\n<%@page import=\"com.sanqing.page.PageConfig\"%>\n<%@page import=\"com.sanqing.util.PageSendRedirect\"%>\n<%@page import=\"com.sanqing.mvc.util.Util\"%>\n<%@page import=\"com.sanqing.sca.service.Protocol\"%>\n<%@page import=\"com.sanqing.util.RequestUtil\"%>\n<%@ page language=\"java\" import=\"java.util.*\" pageEncoding=\"UTF-8\"%>\n<%@ taglib prefix=\"c\" uri=\"http://java.sun.com/jsp/jstl/core\"%>\n<%@ taglib prefix=\"sysmanage\" uri=\"/WEB-INF/tlds/sysmanage.tld\" %>\n<%@ taglib prefix=\"sq\" uri=\"/WEB-INF/tlds/sanqing.tld\"%>\n<%@ taglib prefix=\"fmt\" uri=\"http://java.sun.com/jsp/jstl/fmt\"%>\n<%@ taglib prefix=\"selectZidian\" uri=\"/WEB-INF/tlds/selectZidian.tld\"%>\n<%\n	RequestUtil requestUtil = new RequestUtil();\n	\n	Protocol protocol = requestUtil.pageRequest(\"${context.service}\",\"Query${context.javaBeanName}\",true,request);\n	\n	if(protocol == null){\n		PageSendRedirect.PageError(request, response, \"-1\", \"服务调用失败\", \"/welcome.do\");\n		return;\n	}\n	if(!\"0\".equals(protocol.getRecode())){\n		PageSendRedirect.PageError(request, response, protocol.getRecode(), protocol.getRemsg(), \"/welcome.do\");\n		return;\n	}\n	List<Map<String, Object>> reList = protocol.getTable().get(PageConfig.PAGE_LIST);\n	request.setAttribute(\"reList\",reList);\n%>\n\n<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\n<head>\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\" />\n\n<sysmanage:roleDISPLAY button=\"${context.javaBeanNameLC}/add${context.javaBeanName}.do,${context.javaBeanNameLC}/modify${context.javaBeanName}.do\"\nlogic=\"LOGIC_ADD,LOGIC_EDIT\"/>\n<title>${table.comment}管理</title>\n<link rel=\"stylesheet\" type=\"text/css\" href=\"${base}/css/right.css\" />\n<script type=\'text/javascript\' src=\'${base}/js/CheckText.js\'></script>\n<script type=\'text/javascript\' src=\'${base}/js/checkbox.js\'></script>\n<script type=\'text/javascript\' src=\'${base}/js/verify.js\'></script>\n<script type=\'text/javascript\' src=\'${base}/js/jquery-1.3.2.js\'></script>\n<script type=\'text/javascript\' src=\'${base}/js/common.js\'></script>\n<script type=\'text/javascript\' src=\'${base}/js/calendar.js\'></script>\n<script type=\"text/javascript\">\n    //验证查询条件是否合法\n	function search() {\n#foreach($column in $columns)\n		if (!checkFormItem(getById(\"q${column.columnName}\"),\"${column.checkType}\",#if(${column.checkType}==\'N\' && ${column.precision}!=\'0\')${column.precision}#else${column.length}#end,${column.scale},${column.isNull}, \"${column.comment}\"))return false;\n#end\n		getById(\"QFORM\").action=\"query${context.javaBeanName}.do\";\n		getById(\"QFORM\").submit();\n	}\n</script>\n</head>\n<body>\n<div class=\"right_top\">\n<div class=\"right_top_title\">当前位置：${table.comment}管理 &gt;${table.comment}管理</div>\n</div>\n<div class=\"right_mid1\">\n<div class=\"right_mid1_top\">\n<div class=\"right_mid_title1\">快速查询</div>\n</div>\n<div class=\"right_mid1_mid\">\n\n<form name=\"QFORM\" id=\"QFORM\" method=\"post\">\n<table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\n#set($i=0)\n#foreach($column in $columns)\n#set($i=$i+1)\n#if($i==1 || $i%2==1)\n	<tr>\n#end\n		<td width=\"15%\" align=\"right\">${column.comment}：</td>\n    	<td width=\"35%\">\n#if(${column.checkType}==\'D\')\n    		<input type=\"text\" id=\"q${column.columnName}\" class=\"text\" name=\"q${column.columnName}\" onfocus=\"calendar(event);\" value=\"${jq}{param[\'q${column.columnName}\']}\" style=\"width:200px;\"/>\n#else\n			<input type=\"text\" id=\"q${column.columnName}\" name=\"q${column.columnName}\" class=\"text\"  value=\"${jq}{param[\'q${column.columnName}\']}\"/>\n#end	\n		</td>\n#if($i==$columns.size() || $i%2==0)\n	</tr>\n#end\n#end\n</table>\n\n<div class=\"right_mid1_bottom\">\n<table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\n	<tr>\n		<td width=\"100%\" align=\"center\">\n			<input type=\"submit\"  class=\"but\" value=\"查询\" onclick=\"search();return false;\"/> \n			<input type=\"reset\"  class=\"but\" value=\"重置\" />\n		</td>\n	</tr>\n</table>\n</div>\n</form>\n</div>\n</div>\n<div class=\"right_mid2\">\n<div class=\"right_mid3\">\n	<${myif} test=\"${jq}{LOGIC_ADD==true}\">\n	<input type=\"button\" class=\"but\" value=\"添加\" onclick=\"javascript:location.href=\'add${context.javaBeanName}.do\'\" /> \n    </${myif}>\n</div>\n<form name=\"DFORM\" id=\"DFORM\" method=\"post\" enctype=\"multipart/form-data\">\n<input type=\"hidden\" id=\"zlbh\" name=\"zlbh\" />\n<input type=\"hidden\" id=\"zt\" name=\"zt\" />\n<div class=\"right_mid1_top\">\n<div class=\"right_mid_title2\">详细列表</div>\n</div>\n<div class=\"right_mid1_mid1\">\n<table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" id=\"admintable\">\n	<tr>\n#foreach($column in $columns)\n		<td  align=\"center\" class=\"tab_r\">${column.comment}</td>\n#end\n		<${myif} test=\"${jq}{LOGIC_EDIT==true}\">\n	    <td align=\"center\" class=\"tab_r\">操作</td>\n	    </${myif}>\n	</tr>\n		<tr>\n        <${myfor} var=\"revar\" items=\"${jq}{reList}\" varStatus=\"sn\" >\n#foreach($column in $columns)\n#if(${column.checkType}==\'D\')\n    	<td class=\"tab_r1\"><sq:formatDate dateStr=\"${jq}{revar[\'${column.columnName}\']}\" format=\"yyyy-MM-dd\" /></td>\n#else\n#if(${column.isPk})\n		<td class=\"tab_r1\"><a href=\"view${context.javaBeanName}.do?${column.columnName}=${jq}{revar[\'${column.columnName}\']}\">${jq}{revar[\'${column.columnName}\']}</a></td>\n#else\n		<td class=\"tab_r1\">${jq}{revar[\'${column.columnName}\']}</td>\n#end\n#end\n#end\n		<td class=\"tab_r1\">\n		<${myif} test=\"${jq}{LOGIC_EDIT==true}\">\n			<a href=\"modify${context.javaBeanName}.do?${pkColumn.columnName}=${jq}{revar[\'${pkColumn.columnName}\']}\">修改</a>\n		</${myif}>\n		</td>\n		</${myfor}>\n		</tr>\n</table>\n</div>\n</form>\n<div class=\"right_mid1_bottom1\"><c:import url=\"/include/splitpage.jsp\"></c:import></div>\n</div>\n</body>\n</html>', 'admin');
INSERT INTO `auto_code`.`template_config` (`name`, `save_path`, `suffix`, `file_name`, `content`, `back_user`) VALUES ('jsp_view_model', 'jsp', NULL, 'view${context.javaBeanName}.jsp', '#set($myif=\"c:if\")\n#set($myfor=\"c:forEach\")\n#set($jq=\"$\")\n<%@page import=\"com.sanqing.util.PageSendRedirect\"%>\n<%@page import=\"com.sanqing.sca.service.Protocol\"%>\n<%@page import=\"com.sanqing.util.RequestUtil\"%>\n<%@ page language=\"java\" import=\"java.util.*\" pageEncoding=\"UTF-8\"%>\n<%@ taglib prefix=\"c\" uri=\"http://java.sun.com/jsp/jstl/core\"%>\n<%@ taglib prefix=\"sq\" uri=\"/WEB-INF/tlds/sanqing.tld\"%>\n<%@ taglib prefix=\"fmt\" uri=\"http://java.sun.com/jsp/jstl/fmt\"%>\n<%@ taglib prefix=\"selectZidian\" uri=\"/WEB-INF/tlds/selectZidian.tld\"%>\n<%\n	RequestUtil requestUtil = new RequestUtil();	\n	Protocol protocol = requestUtil.pageRequest(\"${context.service}\",\"View${context.javaBeanName}\",false,request);\n	\n	if(protocol == null){\n		PageSendRedirect.PageError(request, response, \"-1\",\"服务调用失败\",\"/${context.javaBeanNameLC}/query${context.javaBeanName}.do\");\n		return;\n	}\n	if(!\"0\".equals(protocol.getRecode())){\n		PageSendRedirect.PageError(request, response,protocol.getRecode(),protocol.getRemsg(),\"/${context.javaBeanNameLC}/query${context.javaBeanName}.do\");\n		return;\n	}\n	List<Map<String, Object>> list = protocol.getTable().get(\"getObj\");\n	if(list.size() <= 0){\n		PageSendRedirect.PageError(request,response,\"9999\",\"没有找到对应记录\",\"/${context.javaBeanNameLC}/query${context.javaBeanName}.do\");\n		return;\n	}\n	Map<String, Object> map = new HashMap<String, Object>();\n	map = list.get(0);\n	request.setAttribute(\"modify\",map);\n%>\n<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\n<head>\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\" />\n<title>查看${table.comment}</title>\n\n<link rel=\"stylesheet\" type=\"text/css\" href=\"${base}/css/right.css\" />\n\n<script type=\'text/javascript\' src=\'${base}/js/CheckText.js\'></script>\n<script type=\'text/javascript\' src=\'${base}/js/checkbox.js\'></script>\n<script type=\'text/javascript\' src=\'${base}/js/verify.js\'></script>\n<script type=\'text/javascript\' src=\'${base}/third/bootstrap-3.3.5/js/jquery-1.10.2.min.js\'></script>\n<script type=\'text/javascript\' src=\'${base}/js/common.js\'></script>\n<script type=\'text/javascript\' src=\'${base}/js/calendar.js\'></script>\n<script type=\"text/javascript\">\n	//返回\n	function back() {\n		window.location.href = \"query${context.javaBeanName}.do\";\n	}\n</script>\n</head>\n<body>\n	<div class=\"right_top\">\n		<div class=\"right_top_title\">当前位置：${table.comment}管理 &gt;${table.comment}管理 &gt; 查看</div>\n	</div>\n	<div class=\"right_mid\">\n		<div class=\"right_mid1\">\n			<div class=\"right_mid1_top\">\n				<div class=\"right_mid_title2\">${table.comment}详细信息</div>\n			</div>\n			<div class=\"right_mid1_mid\">\n				<table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\n#set($i=0)\n#foreach($column in $columns)\n#set($i=$i+1)\n#if($i==1 || $i%2==1)\n					<tr>\n#end\n                    	<td width=\"15%\" align=\"right\">${column.comment}：</td>\n                        <td width=\"35%\">\n    						${jq}{modify[\'${column.columnName}\']}\n						</td>\n#if($i==$columns.size() || $i%2==0)\n					</tr>\n#end\n#end\n					<tr>\n						<td colspan=\"4\" align=\"center\">\n                            <input type=\"button\" class=\"but\" size=\"32\" value=\"返回\" onclick=\"back()\" />\n                        </td>\n					</tr>\n				</table>\n			</div>\n		</div>\n	</div>\n</body>\n</html>', 'admin');
INSERT INTO `auto_code`.`template_config` (`name`, `save_path`, `suffix`, `file_name`, `content`, `back_user`) VALUES ('jsp_add/modify_result', 'jsp', NULL, 'add${context.javaBeanName}Result.jsp', '<%@page import=\"com.sanqing.util.PageSendRedirect\"%>\n<%@page import=\"com.sanqing.mvc.config.AttrConfig\"%>\n<%@page import=\"com.sanqing.sca.service.Protocol\"%>\n<%@page import=\"com.sanqing.page.PageConfig\"%>\n<%@page import=\"com.sanqing.util.RequestUtil\"%>\n<%@page import=\"java.io.File\"%>\n<%@page import=\"com.sanqing.upload.UploadConfig\"%>\n<%@page import=\"org.apache.commons.fileupload.FileItem\"%>\n<%@page import=\"com.sanqing.upload.CommonsFileUploadUtil\"%>\n<%@page import=\"com.sanqing.upload.UpFileFormEntity\"%>\n<%@page import=\"com.sanqing.mvc.util.Util\"%>\n<%@ page language=\"java\" import=\"java.util.*\" pageEncoding=\"UTF-8\"%>\n<%@ taglib prefix=\"c\" uri=\"http://java.sun.com/jsp/jstl/core\"%>\n<%\n	RequestUtil requestUtil = new RequestUtil();\n	UpFileFormEntity formEntity = CommonsFileUploadUtil.getUploadFile(request);\n	if (formEntity == null) {\n		PageSendRedirect.PageError(request, response, \"9999\", \"文件获取失败\", \"/welcome.do\");\n		return;\n	}\n	Map<String, String> paraMap = formEntity.getParaMap();\n	List<FileItem> fileItems = formEntity.getFileItems();\n	for(FileItem fItem : fileItems){\n		//文件大小大于0\n		if(fItem.getSize()>0){\n			// 得到新的文件名\n			String newFileName = \"${context.javaBeanNameLC}/\"+Util.getOrgMap(request).get(\"KHBH\")+\"/\"+ UploadConfig.getNewName(fItem.getName());\n			String realPath = UploadConfig.UPLOAD_PATH;//eg: d:/template/\n			// 拼接新的物理路径\n			String newPath  = realPath + newFileName;\n			File targetFile = new File(newPath);\n			CommonsFileUploadUtil.checkFilePath(targetFile);\n			// 写入到磁盘\n			fItem.write(targetFile);\n			// 自定义其它有关业务API需要的属性\n			paraMap.put(fItem.getFieldName(), newFileName);\n		}\n	}\n	Protocol protocol = new Protocol(\"${context.service}\",\"Add${context.javaBeanName}\");\n	protocol.putData(PageConfig.PAGE_REQUEST,paraMap);\n	Map<String, String> sessionMap = new HashMap<String, String>();\n	sessionMap = (Map<String, String>)request.getSession().getAttribute(AttrConfig.SESSION_ORG_ID_NAME);\n	protocol.putData(PageConfig.PAGE_SESSION,sessionMap);\n	protocol = requestUtil.jsonRequest(protocol);\n	if(protocol == null){\n		PageSendRedirect.PageError(request, response, \"-1\", \"服务调用失败\", \"/${context.javaBeanNameLC}/query${context.javaBeanName}.do\");\n		return;\n	}\n	if(!\"0\".equals(protocol.getRecode())){\n		PageSendRedirect.PageError(request, response, protocol.getRecode(), protocol.getRemsg(), \"/${context.javaBeanNameLC}/query${context.javaBeanName}.do\");\n		return;\n	}\n%>\n<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\n<head>\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\" />\n<script type=\"text/javascript\">\n	alert(\"操作成功\");\n	window.location.href = \"query${context.javaBeanName}.do\";\n</script>\n</head>\n<body>\n</body>\n</html>', 'admin');


